import React, { useEffect, useState } from 'react';
import { getGigById, applyToGig } from '../services/api';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/GigDetail.css';
import { FaArrowLeft, FaMoneyBillWave, FaTags, FaClipboardList } from 'react-icons/fa';

export default function GigDetail() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [gig, setGig] = useState(null);
  const [error, setError] = useState('');
  const [applyMsg, setApplyMsg] = useState('');
  const [loading, setLoading] = useState(true);

  const handleApply = () => {
    applyToGig(id)
      .then((res) => setApplyMsg(res.data.message))
      .catch((err) => setApplyMsg(err.response?.data?.message || 'Application failed'));
  };

  useEffect(() => {
    getGigById(id)
      .then((res) => {
        setGig(res.data);
        setLoading(false);
      })
      .catch(() => {
        setError('Gig not found');
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div className="gigdetail-container"><div className="loading">Loading...</div></div>;
  if (error) return <div className="gigdetail-container"><div className="error">{error}</div></div>;

  return (
    <div className="gigdetail-container">
      <div className="gigdetail-card">
        <div className="gigdetail-header">
          <h1>{gig.title}</h1>
          <p className="gig-category"><FaTags /> {gig.category || 'General'}</p>
        </div>

        <div className="gigdetail-body">
          <p className="gig-description">
            <FaClipboardList className="icon" /> {gig.description || 'No description provided.'}
          </p>
          <p className="gig-price">
            <FaMoneyBillWave className="icon" /> <strong>{gig.price || gig.budget || 0} USD</strong>
          </p>
        </div>

        <div className="gigdetail-footer">
          {(user.role === 'freelancer') && (
            
          <button className="apply-btn" onClick={handleApply}>
            Apply for Gig
          </button>
          )}
          {applyMsg && <p className="apply-message">{applyMsg}</p>}
          <button className="back-btn" onClick={() => navigate(-1)}>
            <FaArrowLeft /> Back
          </button>
        </div>
      </div>
    </div>
  );
}

