import React, { useContext } from 'react';
import { AuthContext } from '../context/AuthContext';
import { useNavigate } from 'react-router-dom';
import '../styles/Dashboard.css';
import { FaUser, FaSignOutAlt, FaBriefcase, FaPlusCircle, FaUserCircle } from 'react-icons/fa';

export default function Dashboard() {
  const { user, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const userName = user?.user?.name || user?.name || 'Guest';
  const userRole = user?.user?.role || user?.role || 'freelancer'; // Default role fallback

  return (
    
    <div className="dashboard-container">
      <div className="dashboard-card">
        <div className="dashboard-header">
          <FaUserCircle className="user-avatar" />
          <div>
            <h2>Welcome, {userName} ðŸ‘‹</h2>
            <p className="role-badge">{userRole.charAt(0).toUpperCase() + userRole.slice(1)}</p>
          </div>
        </div>

        <div className="dashboard-buttons">
          <button className="dash-btn" onClick={() => navigate('/profile')}>
            <FaUser /> Profile
          </button>
          <button className="dash-btn" onClick={() => navigate('/gigs')}>
            <FaBriefcase /> View Gigs
          </button>
          {userRole === 'client' && (
            <button className="dash-btn" onClick={() => navigate('/create-gig')}>
              <FaPlusCircle /> Post New Gig
            </button>
          )}
          {userRole === 'admin' && (
            <button className="dash-btn" onClick={() => navigate('/admin')}>
              <FaUser /> Admin Panel
            </button>
          )}
        </div>

        <div className="logout-section">
          <button className="logout-btn" onClick={handleLogout}>
            <FaSignOutAlt /> Logout
          </button>
        </div>
      </div>
    </div>
  );
}



